sys_script.script:
(function executeRule(current, previous /*null when async*/) {

// 	var now = new GlideDateTime();
// 	var date = new GlideDateTime(current.u_when_needed);

	var mailOK = validateEmailAddress(current.u_req_for_email);

	if(!mailOK){
		gs.addErrorMessage("Mail inserted is invalid");
		current.setAbortAction(true);
	}

	var NUtils = new NeedItUtils();
// 	var day = date.getLocalDate();
// 	var now_day = now.getLocalDate();

	var isToday = NUtils.dateIsToday(current.u_when_needed);

	//il metodo compareTo Ã¨ pari a zero se le date sono uguali -1 se nel passato...
	if (isToday){
		gs.addErrorMessage("We need some times to manage older requests. Please insert a date in the future, today we are full!");
		current.setAbortAction(true);
		return;

	}

	var isPast = NUtils.dateIsPast(current.u_when_needed);

	if (isPast){
		gs.addErrorMessage("Date must be in the future!");
		current.setAbortAction(true);
	}

})(current, previous);
