sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	dateIsPast: function(date){
		var today = new GlideDateTime();
		var passed = new GlideDateTime(date);
		if(passed.before(today)){
			return true;
		}
		else{return false;}
	},
	dateIsToday: function(date){
		var today = new GlideDateTime();
		var passed = new GlideDateTime(date);
		var day = today.getLocalDate();
		var passed_day = passed.getLocalDate();

		if(passed_day.compareTo(day)==0){
			return true;
		}else{return false;}
	},

    type: 'NeedItUtils'
};
